package grant.guo.leetcode.array

object leetcode13_3sum extends App {
  def threeSum(nums: Array[Int]): List[List[Int]] = {
    val sortedWithIndex = nums.sortWith(_<_).zipWithIndex

    (0 until sortedWithIndex.length).foldLeft(List.empty[List[Int]])((acc, index) => {
      val array = sortedWithIndex.filter(_._2 != index).map(_._1)
      val target = sortedWithIndex(index)._1
      val lst = twoSum(array, if (target > 0) -target else Math.abs(target), 0, array.length - 1)

      val lst_ex = lst.map(l => {
        List(target, l._1, l._2)
      })
      acc ::: lst_ex
    }).filter(_.length == 3).map(lst => {
      lst.sortWith(_<_)
    }).foldLeft(Set[List[Int]]())((acc, lst) => {
      acc + lst
    }).toList
  }


  def twoSum(sortedNums: Array[Int], target: Int, lp: Int, rp: Int): List[(Int, Int)] = {
    (lp >= rp || sortedNums.length == 0)match {
      case true => {
        List.empty[(Int, Int)]
      }
      case false => {
        val v = sortedNums(lp) + sortedNums(rp)
        (v - target) match {
          case v if(v== 0) => {
            val lst3 = twoSum(sortedNums, target, lp+1, rp-1)
            (sortedNums(lp), sortedNums(rp)) :: lst3
          }
          case v if (v>0) => {
            twoSum(sortedNums, target, lp, rp-1)
          }
          case v if(v<0) => {
            twoSum(sortedNums, target, lp + 1, rp)
          }

        }
      }
    }
  }

